mapper.sql:
SELECT
ID, YOUTUBE_NAME, YOUTUBE_URL, UPDATE_DATE, UPDATE_NAME, YOUTUBE_TYPE
FROM T_YOUTUBE
where 1=1


  AND YOUTUBE_NAME = #{youtube_name}


  AND YOUTUBE_URL = #{youtube_url}


  AND UPDATE_DATE = #{update_date}


  AND YOUTUBE_TYPE = #{youtube_type}

mapper.insert:
INSERT INTO T_YOUTUBE
(ID, YOUTUBE_NAME, YOUTUBE_URL, UPDATE_DATE, UPDATE_NAME, YOUTUBE_TYPE)
VALUES ((select max(nvl(id, 0)) + 1), #{youtube_name}, #{youtube_url}, now(), #{update_name}, #{youtube_type});
